            <script>
                $(window).scroll(function() {
                    var windscroll = $(window).scrollTop();
                    if (windscroll >= 100) {
                        $('.digital_course_main').each(function(i) {
                            // The number at the end of the next line is how pany pixels you from the top you want it to activate.
                            if ($(this).position().top <= windscroll - -300) {
                                $('.digital_navigation li.active').removeClass('active');
                                $('.digital_navigation li').eq(i).addClass('active');
                            }
                        });

                    } else {

                        $('.digital_navigation li.active').removeClass('active');
                        $('.digital_navigation li:first').addClass('active');
                    }

                }).scroll();
            </script>